// 定义枚举类型
enum TrafficLight {
  red,
  yellow,
  green
}
// 根据交通灯状态打印消息的函数
void printTrafficLightMessage(TrafficLight light) {
  switch (light) {
    case TrafficLight.red:
      print('Stop!');
      break;
    case TrafficLight.yellow:
      print('Get ready...');
      break;
    case TrafficLight.green:
      print('Go!');
      break;
  }
}
void main() {
  // 枚举类型的示例用法
  TrafficLight currentLight = TrafficLight.green;
  // 打印当前交通灯状态的消息
  printTrafficLightMessage(currentLight);
}
