//extension 定义扩展名称 on 扩展类
extension StringExtension on String {
  //扩展方法
  String capitalize() {
    if (isEmpty) {
      return this;
    }
    // 将字符串的首字母大写
    String topStr = this[0].toUpperCase();

    return '${topStr}${substring(1)}';
  }
}

void main(List<String> args) {
