import { useEffect } from 'react'
import { useRouter } from 'next/router'

// 当前网址为“/”
export default function Page() {
  const router = useRouter()

  useEffect(() => {
    // 始终在第一次渲染后进行导航
    router.push('/?counter=10', undefined, { shallow: true })
  }, [])

  useEffect(() => {
    // counter 变了！
  }, [router.query.counter])
}
