import scala.collection._ // 通配符导入
import scala.collection.Vector
// 选择性导入
import scala.collection.{Vector, Sequence}
// 重命名导入
import scala.collection.{Vector => Vec28}
// 导入java.util包里除Date之外的一切
import java.util.{Date => _, _}
// 文件开头的包名 pkg
package pkg { ... } // 声明这是一个包

(1,2,3) // 元组字面量 (Tuple3)
// 解构绑定：通过模式匹配来解构元组。
var (x,y,z) = (1,2,3)
// ❌ BAD 潜在风险：整个元组被赋值给了每一个变量
var x,y,z = (1,2,3)
// 列表 (不可变)
var xs = List(1,2,3)
xs(2) // 用括号索引 (slides)
1 :: List(2,3) // Cons（构成）
1 to 5 /* 等价于 => */ 1 until 6
// Range类型（语法糖）
1 to 10 by 2
// Unit类型的唯一成员 (相当于 C/Java 里的void)
() (空括号)

import scala.util.control.Breaks._
breakable {
  for (x <- xs) {
    if (Math.random < 0.1) break
  }
}

for (x <- xs if x%2 == 0) yield x*10
// 等价于
xs.filter(_%2 == 0).map(_*10)

for ((x,y) <- xs zip ys) yield x*y
// 等价于
(xs zip ys) map { case (x,y) => x*y }

for (x <- xs; y <- ys) yield x*y
// 等价于
xs flatMap {x => ys map {y => x*y}}

for (x <- xs; y <- ys) {
   println("%d/%d = %.1f".format(x, y, x/y.toFloat))
}

for (i <- 1 to 5) {
  println(i)
}

for (i <- 1 until 5) {
  println(i)
}

// ✅ GOOD
(xs zip ys) map { case (x,y) => x*y }
// ❌ BAD
(xs zip ys) map( (x,y) => x*y )

val v42 = 42
Some(3) match {
  case Some(v42) => println("42")
  case _ => println("Not 42")
}

val v42 = 42
Some(3) match {
  case Some(`v42`) => println("42")
  case _ => println("Not 42")
}

val UppercaseVal = 42
Some(3) match {
case Some(UppercaseVal) => println("42")
  case _ => println("Not 42")
}

