// `defineComponent`用于IDE推导类型
export default defineComponent({
// setup 用于组合式 API 的特殊钩子函数
setup() {
const state = reactive({ count: 0 });

// 暴露 state 到模板
return {
state
};
},
});

export default defineComponent({
setup() {
const state = reactive({ count: 0 });

function increment() {
state.count++;
}

// 不要忘记同时暴露 increment 函数
return {
state,
increment
};
},
})

